
// Functie voor een welkomstbericht
function welcomeMessage() {
    const welcomeElement = document.getElementById("welcome-message");
    
    if (welcomeElement) {
        welcomeElement.innerHTML = "Welkom bij Paws for Life! Bedankt voor je bezoek.";
        welcomeElement.style.display = "block";  // Zorg ervoor dat de welkomstboodschap zichtbaar is
    }
}

// Functie om de datum van vandaag te tonen
function showCurrentDate() {
    const currentDate = new Date();
    const dateElement = document.getElementById("current-date");

    if (dateElement) {
        dateElement.textContent = `Vandaag is het ${currentDate.toLocaleDateString('nl-NL', {
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        })}.`;
    }
}

// Functie voor de adoptiepagina om dieren te tonen
function showAdoptableAnimals() {
    if (document.body.classList.contains("adoptie-pagina")) {
        const animals = [
            { name: "Max", type: "Hond", breed: "Labrador", image: "data:image/jpeg;base64,", description: "Max is een speelse Labrador die op zoek is naar een nieuw huis." },
            { name: "Bella", type: "Kat", breed: "Britse Korthaar", image: "data:image/jpeg;base64,", description: "Bella is een rustige kat die het heerlijk vindt om geknuffeld te worden." },
            { name: "Milo", type: "Hond", breed: "Beagle", image: "data:image/jpeg;base64,", description: "Milo is energiek en houdt van buiten spelen." },
            { name: "Luna", type: "Kat", breed: "Siamees", image: "data:image/jpeg;base64,", description: "Luna is een nieuwsgierige kat die dol is op avontuur." }
        ];

        const animalList = document.getElementById("animal-list");

        animals.forEach(animal => {
            const animalItem = document.createElement("div");
            animalItem.classList.add("dier-item");
            animalItem.innerHTML = `
                <img src="${animal.image}" alt="${animal.name}">
                <h3>${animal.name} - ${animal.breed}</h3>
                <p>${animal.description}</p>
                <button class="adopt-btn">Adopteer</button>
            `;
            animalList.appendChild(animalItem);
        });
    }
}

// Functie voor de over ons pagina om organisatie-informatie te tonen
function showAboutUsInfo() {
    const aboutUs = {
        mission: "Onze missie is het redden van dieren in nood en het geven van een tweede kans aan dieren die geen thuis hebben.",
        vision: "Wij geloven in een wereld waar elk dier liefde, zorg en een veilig thuis verdient.",
        founded: "Opgericht in 2020, hebben wij al duizenden dieren geholpen een beter leven te krijgen."
    };

    const missionElement = document.getElementById("mission");
    const visionElement = document.getElementById("vision");
    const foundedElement = document.getElementById("founded");

    if (missionElement) missionElement.textContent = aboutUs.mission;
    if (visionElement) visionElement.textContent = aboutUs.vision;
    if (foundedElement) foundedElement.textContent = aboutUs.founded;
}

// Functie om het formulier af te handelen
document.getElementById("contact-form")?.addEventListener("submit", function(event) {
    event.preventDefault(); // Voorkomt dat het formulier verzendt zonder validatie

    const name = document.getElementById("name").value;
    const email = document.getElementById("email").value;
    const message = document.getElementById("message").value;

    alert(`Bedankt voor je bericht, ${name}! We nemen zo snel mogelijk contact met je op.`);
    
    document.getElementById("contact-form")?.reset();
});

// Algemene functie die op elke pagina wordt uitgevoerd
function initializePage() {
    showCurrentDate();
    welcomeMessage();
    showAdoptableAnimals();
    showAboutUsInfo();  // Alleen voor pagina's waar deze info nodig is
}

window.onload = initializePage;
